import axios from "axios";

const apiUrl = "http://localhost:5000";

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/tasks`);
    return result.data;
  },

  // 2️⃣ הוספת משימה חדשה
  addTask: async (name) => {
    const newTask = { name, isComplete: false };
    const result = await axios.post(`${apiUrl}/tasks`, newTask);
    return result.data;
  },

  // 3️⃣ עדכון סטטוס משימה
  setCompleted: async (id, isComplete) => {
    const updatedTask = { name: "", isComplete }; // השם לא משתנה כאן
    await axios.put(`${apiUrl}/tasks/${id}`, updatedTask);
  },

  // 4️⃣ מחיקת משימה
  deleteTask: async (id) => {
    await axios.delete(`${apiUrl}/tasks/${id}`);
  }
};
